import React, { Component } from 'react';
import styled from 'styled-components';
import { Button, ButtonToolbar } from 'react-bootstrap';
import InfoBar from './components/InfoBar';
import './App.css';


const Title = styled.h1`
  text-align: center
`;

class App extends Component {
  render() {
    /*
    // Create About Button
    const aboutButton = (
      <input
        type="button"
        value="About"
      />
    );

    // Create Team Button
    const teamButton = (
      <input
        type="button"
        value="Team"
      />
    );

    // Create Contact Us Button
    const contactUsButton = (
      <input
        type="button"
        value="Contact Us"
      />
    );

    //Create the InfoBar
    let infoBar = (<div>{aboutButton} {teamButton} {contactUsButton}</div>)
    */

    // Create the Encode Image Button
    const encoderButton = (
      <input
        type="button"
        value="Encode Image"
      />
    );

    // Create the Decode Image Button
    const decoderButton = (
      <input
        type="button"
        value="Decode Image"
      />
    );

    let buttons = (<div>{encoderButton} {decoderButton}</div>)


    return (
      <div className="App">
        <Title>Steganography Project - The Encoder</Title>
        <InfoBar />
        {buttons}
        <ButtonToolbar>
          <Button variant = "info">Encode Image</Button>
          <Button variant = "info">Decode Image</Button>
        </ButtonToolbar>

      </div>
    );
  }
}

export default App;
